div id="avatar">\n<a href="/projects/jeffheaton/artificial-intelligence-for-humans-vol-2-nature-al/creator_bio" class="remote_modal_dialog" data-modal-class="modal_project_by" data-modal-title="Biography"><img alt="Inst_jh.medium" class="avatar-medium" height="160" src="https://s3.amazonaws.com/ksr/avatars/6268417/inst_jh.medium.jpg?1391779838" width="160" /></a>\n</div>\n<div id="creator-name">\n<h6>Project by</h6>\n<h5><a href="/projects/jeffheaton/artificial-intelligence-for-humans-vol-2-nature-al/creator_bio" class="remote_modal_dialog" data-modal-class="modal_project_by" data-modal-title="Biogra', '', '2014-02-24 10:11:38');
INSERT INTO `links` (`id`, `user_id`, `folder_id`, `username_id`, `grid`, `ico`, `url`, `title`, `thumb`, `meta`, `site`, `desc`, `html_source`, `hash`, `timestamp`) VALUES
