INSERT INTO `links` (`id`, `user_id`, `folder_id`, `username_id`, `grid`, `ico`, `url`, `title`, `thumb`, `meta`, `site`, `desc`, `html_source`, `hash`, `timestamp`) VALUES
(189, 1, 0, 'joe.chen.1', '1,27', 'http://tw.img.nextmedia.com/appledaily/pinsite/64x64.ico', 'http://www.appledaily.com.tw/appledaily/article/headline/20140215/35642629/', '我的陳文茜：這個國家太對不起年輕人（陳文茜） | 蘋果日報', 'http://twimg.edgesuite.net//images/twapple/640pix/20140215/LB24/LB24_004.jpg', '{"Title":"我的陳文茜：這個國家太對不起年輕人（陳文茜） | 蘋果日報","description":"宛如是一名念研究所時充滿文創理想的年輕人，畢業時在台北找到了一份夢寐以求的文創工作，月薪不算低4萬；她於台北市郊外租了一個小房間，開啟人生青春夢。一張床、一張書桌、房租8千元，上班地點大安區，搭公車加捷運約一小時。她每天7點出門，晚上加班至10點才下班，回到家裡已經夜裡11點。洗把臉，沖個澡，想把白天工作不熟悉的地方翻書查閱一下，厚重眼皮已然垂下來。她的工作是文創，但沒有太多時間寧靜蓄累自己的創意能量。她知道自己正在學習，但也正被掏空。","og:description":"宛如是一名念研究所時充滿文創理想的年輕人，畢業時在台北找到了一份夢寐以求的文創工作，月薪不算低4萬；她於台北市郊外租了一個小房間，開啟人生青...","keywords":"論壇,頭條要聞","Robots":"INDEX,FOLLOW","fb:admins":"100003491041452","fb:app_id":"244889285591923","og:image":"http://twimg.edgesuite.net//images/twapple/640pix/20140215/LB24/LB24_004.jpg","url":{"source":"http://www.appledaily.com.tw/appledaily/article/headline/20140215/35642629/","protocol":"http","host":"www.appledaily.com.tw","port":"","relative":"/appledaily/article/headline/20140215/35642629/","path":"/appledaily/article/headline/20140215/35642629/","directory":"/appledaily/article/headline/20140215/35642629/","file":"","query":"","fragment":""}}', '', '', 'HTTP/1.1 200 OK\r\nDate: Mon, 24 Feb 2014 10:21:46 GMT\r\nServer: Apache\r\nVary: Accept-Encoding\r\nTransfer-Encoding: chunked\r\nContent-Type: text/html; charset=utf-8\r\n\r\n<!DOCTYPE html>\n<!--[if lt IE 7 ]> <html lang="zh-TW" class="ie6 ielt8"> <![endif]-->\n<!--[if IE 7 ]>    <html lang="zh-TW" class="ie7 ielt8"> <![endif]-->\n<!--[if IE 8 ]>    <html lang="zh-TW" class="ie8"> <![endif]-->\n<!--[if (gte IE 9)|!(IE)]><!--> <html lang="zh-TW"> <!--<![endif]-->\n<head>\n    <meta charset="utf-8" />\n    <title>我的陳文茜：這個國家太對不起年輕人（陳文茜） | 蘋果日報</title>\n    <meta name="Title" content="我的陳文茜：這個國家太對不起年輕人（陳文茜） | 蘋果日報"/>\n    <meta name="description" content="宛如是一名念研究所時充滿文創理想的年輕人，畢業時在台北找到了一份夢寐以求的文創工作，月薪不算低4萬；她於台北市郊外租了一個小房間，開啟人生青春夢。一張床、一張書桌、房租8千元，上班地點大安區，搭公車加捷運約一小時。她每天7點出門，晚上加班至10點才下班，回到家裡已經夜裡11點。洗把臉，沖個澡，想把白天工作不熟悉的地方翻書查閱一下，厚重眼皮已然垂下來。她的工作是文創，但沒有太多時間寧靜蓄累自己的創意能量。她知道自己正在學習，但也正被掏空。" />\n    <meta name="og:description" content="宛如是一名念研究所時充滿文創理想的年輕人，畢業時在台北找到了一份夢寐以求的文創工作，月薪不算低4萬；她於台北市郊外租了一個小房間，開啟人生青..." />\n    <meta name="keywords" content="論壇,頭條要聞" />\n    <meta name="Robots" content="INDEX,FOLLOW" />\n    <meta property="fb:admins" content="100003491041452"/>\n    <meta property="fb:app_id" content="244889285591923"/>\n    <link rel="canonical" href="http://www.appledaily.com.tw/appledaily/article/headline/20140215/35642629/"/>\n    <meta property="og:image" content="http://twimg.edgesuite.net//images/twapple/640pix/20140215/LB24/LB24_004.jpg"/><link href="http://twimg.edgesuite.net//images/twapple/640pix/20140215/LB24/LB24_004.jpg" rel="image_src" type="image/jpeg">    <link rel="shortcut icon" href="http://tw.img.nextmedia.com/appledaily/pinsite/64x64.ico">\n    <link rel="shortcut icon" type="image/x-icon" href="http://tw.img.nextmedia.com/appledaily/pinsite/64x64.ico">\n    <link rel="icon" type="image/ico" href="http://tw.img.nextmedia.com/appledaily/pinsite/64x64.ico">\n    <link rel="stylesheet" href="http://tw.img.nextmedia.com/appledaily/images/css/style.css">\n    <link rel="stylesheet" href="http://tw.img.nextmedia.com/appledaily/images/css/modules.css">\n    <link rel="stylesheet" href="http://tw.img.nextmedia.com/appledaily/images/css/article_style.css" />\n    <link rel="stylesheet" href="http://tw.img.nextmedia.com/appledaily/images/css/jquery.lightbox-0.5.css" />\n    <style  type="text/css">\n        #mediaspace{position:absolute;top:0;left:0;display:block;width:630px;height:355px;padding:5px;}\n        #unImage {position:absolute;display:none;width:590px;height:350px;padding-left:25px;padding-top:7px;left:0;top:0;}\n        #newimglayer {position:absolute;top:0;left:0;z-index:99;width:590px;height:350px;padding-left:25px;padding-top:7px;}\n        #unImage img,#newimglayer img{width:590px;height:350px;border:0